import React, { useEffect, useState } from 'react'
import { useRecoilValue } from 'recoil'
import { socket } from './atoms'
import './styles.css'

const Game = () => {

    const [number, setNumber] = useState(0)
    const [firstNumber, setFirstNumber] = useState(0)
    const [secondNumber, setSecondNumber] = useState(0)

    const connectedSocket = useRecoilValue(socket)







    useEffect(() => {
            connectedSocket.on('generatedNum', (info) => {
                console.log(info)
                setNumber(info)
            })
    }, [])

    useEffect(() => {
        setFirstNumber(-(Math.floor(Math.random() * 15)))
        setSecondNumber(-(Math.floor(Math.random() * 10)))
    },[number])

    const decrement = (num) => {
        setNumber(number - num)
    }

    return (
        <div className = 'game'>
            <div>
                <h1>{number}</h1>
            </div>
            <div>
                <button className = 'game_button' onClick = {(firstNumber) => decrement}>{firstNumber}</button>
                <button className = 'game_button' onClick = {(secondNumber) => decrement}>{secondNumber}</button>
                <button className = 'game_button' onClick = {() => decrement}>{-1}</button>
            </div>

        </div>
    )
}

export default Game
