import React, { useEffect, useState } from 'react'
import { useRecoilValue } from 'recoil'
import { socket } from './atoms'
import './styles.css'

const Game = () => {

    const [number, setNumber] = useState(0)
    const [firstNumber, setFirstNumber] = useState(0)
    const [secondNumber, setSecondNumber] = useState(0)

    const [numOfUsers, setNumOfUsers] = useState(0)

    const connectedSocket = useRecoilValue(socket)

    useEffect(() => {

        connectedSocket.on('numOfUsers', (info) => {
            setNumOfUsers(info)
            console.log(numOfUsers)
        })

    }, [numOfUsers])







    useEffect(() => {
            connectedSocket.on('generatedNum', (info) => {
                console.log(info)
                setNumber(info[0])
                setFirstNumber(info[1])
                setSecondNumber(info[2])
            })


    }, [])

    useEffect(() => {
        setFirstNumber((Math.floor(Math.random() * 15)))
        setSecondNumber((Math.floor(Math.random() * 10)))
    },[])

    const decrement = (num) => {
        setNumber(number - num)
    }

    return (
        <div className = 'game'>
            <div>
                <h1>{number}</h1>
            </div>
            <div>
                <button className = 'game_button' onClick = {() => decrement(firstNumber)}>-{firstNumber}</button>
                <button className = 'game_button' onClick = {() => decrement(secondNumber)}>-{secondNumber}</button>
                <button className = 'game_button' onClick = {() => decrement(1)}>{-1}</button>
            </div>

        </div>
    )
}

export default Game
